def wavesurfer_export_to_array(filename):
    import re, numpy as np
    f = open(filename, 'r')
    data = re.split('\s+',f.read())[1::2]
    data = [float(i) for i in data]
    return np.array(data)

if __name__=='__main__':
    import sys, getopt 
    argv = sys.argv[1:]
    opts, args = getopt.getopt(argv,"fl:",["fft=","lpc="])
    
    lpc = None
    fft = None

    if len(opts) < 2:
      smooth_demo()
      sys.exit(0)

    for opt, arg in opts:
      if opt in ('-f', '--fft'):
        fft = wavesurfer_export_to_array(arg)
      elif opt in ('-l', '--lpc'):
        lpc = wavesurfer_export_to_array(arg)

    smooth_demo(fft=fft, lpc=lpc)
